var ph=url+"/top/";
var qb=url+"/full-1/";
var html=java.ajax(fl);
var src=java.ajax(ph);
var qbl=java.ajax(qb);

if(html&&src&&qbl){
a=org.jsoup.Jsoup.parse(html).select(".content li a");
b=org.jsoup.Jsoup.parse(src).select(".content li a");
c=org.jsoup.Jsoup.parse(qbl).select(".content li a");
push("🌹分类🌹",null,1)
    for(i in a){
        title=a[i].text()
        url=a[i].attr("href")
        size=0.25;
  url=String(url).replace("1/","{{page}}/")
        push(title,url,size)
    };
 push("🌹排行🌹",null,1)
    for(i in b){
        title=b[i].text()
        url=b[i].attr("href")
  url=String(url).replace("1/","{{page}}/")
        size=0.25;
        push(title,url,size)
    }
push("🌹全本🌹","/full/{{page}}/",1)

}else{
  java.toast("发现列表获取失败！")
}

